import { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { ChevronLeftIcon, ChevronRightIcon } from "@heroicons/react/solid";
import InputField from "./inputField";
import Button from "./button";
import { UploadIcon, PlusCircleIcon } from "@heroicons/react/solid";

const Table = ({
  limit,
  tableData,
  tableHead,
  renderData,
  renderHead,
  tableTitle,
  search,
  createButtonHandler,
  importButtonHandler,
  assignButtonHandler,
}) => {
  let pages = 1;
  let range = [];

  const [dataShow, setDataShow] = useState(
    limit && tableData ? tableData.slice(0, Number(limit)) : tableData
  );
  const [currPage, setCurrPage] = useState(0);
  const [searchQuery, setSearchQuery] = useState(""); // New state for search query

  useEffect(() => {
    setDataShow(
      limit && tableData ? tableData.slice(0, Number(limit)) : tableData
    );
  }, [limit, tableData]);

  useEffect(() => {
    // Filter tableData based on search query
    const filteredData = tableData.filter((item) =>
      item.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setDataShow(filteredData.slice(0, Number(limit)));
    setCurrPage(0);
  }, [searchQuery, limit, tableData]);

  if (limit !== undefined) {
    let page = Math.floor(tableData.length / Number(limit));
    pages = tableData.length % limit === 0 ? page : page + 1;
    range = [...Array(pages).keys()];
  }

  const selectPage = (page) => {
    const start = limit * page;
    const end = start + limit;

    setDataShow(tableData.slice(start, end));

    setCurrPage(page);
  };

  const nextPage = () => {
    const start = limit * (currPage + 1);
    const end = start + limit;
    setDataShow(tableData.slice(start, end));
    setCurrPage((prev) => prev + 1);
  };

  const prevPage = () => {
    const start = limit * (currPage - 1);
    const end = start + limit;
    setDataShow(tableData.slice(start, end));
    setCurrPage((prev) => prev - 1);
  };

  const debounce = (func, wait) => {
    let timeout;
    return function (...args) {
      const context = this;
      if (timeout) clearTimeout(timeout);
      timeout = setTimeout(() => {
        timeout = null;
        func.apply(context, args);
      }, wait);
    };
  };

  const searchWithDebounce = debounce(search, 1000);

  return (
    <section className="antialiased text-gray-600 mt-20 px-4 rounded-xl">
      <div className="flex flex-col justify-center h-full rounded-xl">
        <div className="w-full mx-auto bg-white shadow-lg rounded-sm border border-gray-200">
          <header className="px-5 py-4 border-b border-gray-100">
            <h2 className="font-semibold text-gray-800">{tableTitle}</h2>
            <div className="w-full flex justify-between">
              {search && (
                <InputField
                  type="text"
                  placeholder="Search..."
                  onChange={(e) => setSearchQuery(e.target.value)} // Update search query state
                  className="w-[80px] sm:w-1/5 rounded-md mt-1 border-gray-300"
                />
              )}
              {/* Rest of the code */}
            </div>
          </header>
          {/* Rest of the code */}
        </div>
      </div>
    </section>
  );
};

Table.propTypes = {
  limit: PropTypes.number,
  tableData: PropTypes.array,
  tableHead: PropTypes.array,
  renderData: PropTypes.func,
  renderHead: PropTypes.func,
  tableTitle: PropTypes.string,
  search: PropTypes.func,
  createButtonHandler: PropTypes.func,
  importButtonHandler: PropTypes.func,
  assignButtonHandler: PropTypes.func,
};

export default Table;
